import { ArrowLeftOutlined, ArrowRightOutlined } from "@mui/icons-material";
import { styled } from "styled-components"

const Container =styled.div`
  width: 100%;
  height: 100vh;
  display: flex;
  position: absolute;
  overflow: hidden;
`;

const Arrow =styled.div`
  width: 50px;
  height: 50px;
  background-color: #fff7f7;
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  position: absolute;
  top: 0;
  bottom: 0;
  left: ${props=> props.direction ==="left" && "10px"};
  right: ${props=> props.direction ==="right" && "10px"};
  margin: auto;
  cursor: pointer;
  opacity: 0.5;
`;

const Wrapper = styled.div`
  height: 100px;
  display: flex;
  transform: translateX(-300px)
`;

const Slide = styled.div`
  width: 100vw;
  height: 100vh;
  display: flex;
  align-items: center;
  background-color:#${props =>props.bg};
`;

const ImgContainer = styled.div`
  height: 100%;
  flex: 1;
`;

const Image=styled.img`
  height: 80%;
  width: 100%;
`;

const InfoContainer = styled.div`
  flex: 1;
  padding: 50px;
`;

const Title= styled.h1`
  font-size: 70px;
`
const Desc= styled.p`
  margin: 50px 0;
  font-size: 20px;
  font-weight: 500;
  letter-spacing: 3px;
`
const Button= styled.button`
  padding:10px;
  font-size: 20px;
  background-color: transparent;
  cursor: pointer;
`

const Slider = () => {

  const handleClick = (direction) => {

  }
  return (
    <Container>
        <Arrow direction="left" onClick={()=>handleClick("left")}>
            <ArrowLeftOutlined/>
        </Arrow>
        <Wrapper>
          <Slide bg="f5fafd">
            <ImgContainer>
            <Image src="../assets/images/megasale.jpeg"  />
            </ImgContainer>
            <InfoContainer>
              <Title>
                HIGH LAND 8-9 JUL
              </Title>
              <Desc>
                LIKE & SHARE FOR TICKET GIVE AWAY
              </Desc>
              <Button>
                SHOW NOW
              </Button>
            </InfoContainer>
            </Slide>
            <Slide bg="f5faed">
            <ImgContainer>
            <Image src="../assets/images/cover.png"  />
            </ImgContainer>
            <InfoContainer>
              <Title>
                Vegan Chocolates
              </Title>
              <Desc>
                NEW ARRIVALS
              </Desc>
              <Button>
                SHOW NOW
              </Button>
            </InfoContainer>
            </Slide>
        </Wrapper>
        <Arrow direction="right" onClick={()=>handleClick("right")}>
            <ArrowRightOutlined/>
        </Arrow>
    </Container>
  )
}

export default Slider
