import { styled } from "styled-components"
import Navbar from "../components/Navbar";
import Announcement from "../components/Announcement"
import Newsletter from "../components/Newsletter";
import Footer from "../components/Footer";


const Container = styled.div``;

const Wrapper = styled.div`
  padding: 50px;
  display: flex;
`;

const ImgContainer = styled.div`
  flex: 1;
`;

const Image = styled.img`
  width: 100%;
`;

const InfoContainer = styled.div`
  flex: 1;
  padding: 0px 50px;
`;

const Title = styled.h1`
  font-weight: 200;
`;

const Desc = styled.p`
  margin:20px 0px;
`;

const Price = styled.span`
  font-weight: 100;
  font-size: 40px;
`;
const FilterContainer = styled.div`
  width: 50%;
  display: flex;
  justify-content: space-between;
`;

const Filter = styled.div`

`;

const FilterTitle =styled.span`

`;

const FilterQty = styled.option`

`;
const FilterGradeTitle = styled.select``;
const FilterGrade = styled.option`

`;



const Product = () => {
  return (
  <Container>
    <Navbar/>
    <Announcement/>
    <Wrapper>
      <ImgContainer>
          <Image src="../assets/images/theruntz.webp"/>
      </ImgContainer>
      <InfoContainer>
        <Title> The Runtz</Title>
        <Desc> Sativa Dominant 60% THC 22% CBD 0.2%</Desc>
        <Price> B 800</Price>
        <FilterContainer>
          <Filter>
            <FilterTitle>Gram</FilterTitle>
            <FilterQty>1</FilterQty>
            <FilterQty>3.5</FilterQty>
            <FilterQty>7</FilterQty>
            <FilterQty>28</FilterQty>
            </Filter>
          <Filter>
            <FilterTitle>Grade</FilterTitle>
            <FilterGrade>Top-Grade</FilterGrade>
            <FilterGrade>Pop-Corn</FilterGrade>
          </Filter>
        </FilterContainer>
      </InfoContainer>
    </Wrapper>
    <Newsletter/>
    <Footer/>
  </Container>
  )
}

export default Product
