import { styled } from "styled-components"
import Announcement from "../components/Announcement";
import Navbar from "../components/Navbar";
import Footer from "../components/Footer";
import { Add, Remove } from "@mui/icons-material";

const Container = styled.div`

`;

const Wrapper = styled.div`
    padding: 20px;
`;

const Title = styled.h1`
    font-weight: 300;
    text-align: center;
`;

const Top = styled.div`
    display: flex;
    align-items: center;
    justify-content: space-between;
    padding: 20px;
`;

const TopButton = styled.button`
    padding: 10px;
    font-weight: 600;
    cursor: pointer;
    border: ${props=>props.type ==="filled" && "none"};
    background-color: ${props=>props.type ==="filled" ? "black" : "transparent"};
    color:${props=>props.type ==="filled" && "white"};
`;

const TopTexts = styled.div``;

const TopText = styled.span`
    text-decoration: underline;
    cursor: pointer;
    margin: 0 10px;
`;

const Bottom = styled.div`
    display: flex;
    justify-content:space-between ;
`;

const Info = styled.div`
    flex:3;
`;

const Product = styled.div`
    display: flex;
    justify-content: space-between;
`;
const ProductDetail = styled.div`
    flex:2;
    display: flex;
`;
const Image = styled.img`
    width: 200px;

`;
const PriceDetail = styled.div`
    flex:1;
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
`;

const ProductAmountContainer=styled.div`
    display: flex;
    align-items: center;
    margin-bottom: 20px;
`;
const ProductAmount=styled.div`
    font-size: 24px;
    margin: 5px;
`;
const ProductPrice =styled.div`
    font-size: 30px;
    font-weight: 200;
`;

const Hr= styled.hr`
    background-color: #eee;
    border:none;
    height: 1px;
`;

const Details = styled.div`
    padding: 20px;
    display: flex;
    flex-direction: column;
    justify-content: space-around;
`;
const ProductName = styled.span``;
const ProductId = styled.span``;
const ProductColor= styled.div`
    width:20px;
    height: 20px;
    display: flex;
    border-radius: 50%;
    background-color: ${props=>props.color};
    border: 1px solid black;
`;

const Summary = styled.div`
    flex: 1;
`;



const Cart = () => {
  return (
    <Container>
       <Navbar/>
       <Announcement/>
       <Wrapper>
        <Title>YOUR BAG</Title>
        <Top>

            <TopButton>CONTINUE SHOPPING</TopButton>
            <TopTexts>
                <TopText>Shopping Bag (4)</TopText>
                <TopText>Your Wishlist (0)</TopText>
            </TopTexts>
            <TopButton type="filled">CHECKOUT NOW</TopButton>
        </Top>
        <Bottom>
            <Info>
                <Product>
                    <ProductDetail>

                        <Image src="../assets/images/MCH003B.png"/>
                        <Details>
                            <ProductName><b>Product:</b> FOUR TWENTY BASEBALL CAP</ProductName>
                            <ProductId><b>ID:</b> MCH003B</ProductId>
                            <ProductColor color="white"/>
                        </Details>
                    </ProductDetail>
                    <PriceDetail>
                        <ProductAmountContainer>
                            <Add/>
                            <ProductAmount>4</ProductAmount>
                            <Remove/>
                            </ProductAmountContainer>
                            <ProductPrice>฿ 2,760 </ProductPrice>
                    </PriceDetail>
                </Product>
                <Hr/>
                <Product>
                    <ProductDetail>

                        <Image src="../assets/images/MCH003C.png"/>
                        <Details>
                            <ProductName><b>Product:</b> FOUR TWENTY BASEBALL CAP</ProductName>
                            <ProductId><b>ID:</b> MCH003C</ProductId>
                            <ProductColor><b>Color:</b> DARK GREEN</ProductColor>
                        </Details>
                    </ProductDetail>
                    <PriceDetail>
                        <ProductAmountContainer>
                            <Add/>
                            <ProductAmount>1</ProductAmount>
                            <Remove/>
                            </ProductAmountContainer>
                            <ProductPrice>฿ 690 </ProductPrice>
                    </PriceDetail>
                </Product>
                <Hr/>
                <Product>
                    <ProductDetail>

                        <Image src="../assets/images/theruntz.webp"/>
                        <Details>
                            <ProductName><b>Product:</b> THE RUNTZ</ProductName>
                            <ProductId><b>ID:</b> CF038TG1</ProductId>
                            <ProductColor><b>Color:</b> DARK GREEN</ProductColor>
                        </Details>
                    </ProductDetail>
                    <PriceDetail>
                        <ProductAmountContainer>
                            <Add/>
                            <ProductAmount>1</ProductAmount>
                            <Remove/>
                            </ProductAmountContainer>
                            <ProductPrice>฿ 700 </ProductPrice>
                    </PriceDetail>
                </Product>
            </Info>
            <Summary>summary</Summary>
        </Bottom>
       </Wrapper>
       <Footer/>
    </Container>
  )
}

export default Cart
